record.field:
for rec in records.sudo():
  if rec.requested_availability_id:
    env['scheduled.visit'].search([('visit_request_id', '=', rec.id)]).unlink()
    scheduled = env['scheduled.visit'].create({'visit_request_id': rec.id, 'visit_availability_slot_id': rec.requested_availability_id.id, 'visitor_screening_ids': rec.screening_ids.ids})
    rec['scheduled_visit_id'] = scheduled.id
